if(!msg.payload || typeof msg.payload !== 'object') return null;
const db = msg.payload;
flow.set('courses', db.courses || []);
flow.set('universities', db.universities || []);
flow.set('newCourses', db.newCourses || []);
flow.set('lastUpdated', db.lastUpdated || 'Never');
return { payload: 'restored' };

// Load Existing Data
const courses = flow.get('courses') || [];
const universities = flow.get('universities') || [];
const newCourses = flow.get('newCourses') || [];
const lastUpdated = flow.get('lastUpdated') || 'Never';

if (courses.length === 0) {
    return [
        { payload: 0, topic: "total_courses" },
        { payload: 0, topic: "total_universities" },
        { payload: lastUpdated, topic: "last_updated" },
        { payload: [], topic: "university_table" },
        { payload: [], topic: "new_courses" }
    ];
}

// Map each uni to include sn, category, and counts
const uniData = universities
    .sort((a, b) => a.name.localeCompare(b.name))
    .map((uni, idx) => {
        // determine category
        const name = uni.name.toLowerCase();
        let category = 'University';
        if (name.includes('hochschule') || name.includes('university of applied sciences') || name.includes('fachhochschule')) {
            category = 'University of Applied Sciences';
        } else if (name.includes('technische') || name.includes('technical university')) {
            category = 'Technical University';
        } else if (name.includes('kunst') || name.includes('art') || name.includes('music')) {
            category = 'Arts/Music University';
        }
        return {
            sn: idx + 1,
            name: uni.name,
            category: category,
            courses: uni.courses
        };
    });

return [
    { payload: courses.length, topic: "total_courses" },
    { payload: universities.length, topic: "total_universities" },
    { payload: lastUpdated, topic: "last_updated" },
    { payload: uniData, topic: "university_table" },
    { payload: newCourses.slice(0, 10), topic: "new_courses" }
];

// Store filter values in context
const topic = msg.topic;
const value = msg.payload;

// Initialize filters if not exist
let filters = flow.get('courseFilters') || {
    degree: ['all'],
    fee: 'all',
    language: 'all'
};

// Update the specific filter
if (topic === 'filter_degree') {
    filters.degree = Array.isArray(value) ? value : [value];
} else if (topic === 'filter_fee') {
    filters.fee = value;
} else if (topic === 'filter_language') {
    filters.language = value;
}

// Store updated filters
flow.set('courseFilters', filters);

// Get all courses and universities
const allCourses = flow.get('courses') || [];
const allUniversities = flow.get('universities') || [];

// Apply filters to courses
let filteredCourses = allCourses.filter(course => {
    // Degree filter
    if (!filters.degree.includes('all')) {
        const degreeMatch = filters.degree.some(deg => {
            if (deg === 'bachelor') {
                return course.degree && course.degree.toLowerCase().includes('bachelor');
            } else if (deg === 'master') {
                return course.degree && course.degree.toLowerCase().includes('master');
            }
            return false;
        });
        if (!degreeMatch) return false;
    }

    // Fee filter
    if (filters.fee !== 'all') {
        const feeText = (course.fees || '').toLowerCase();
        if (filters.fee === 'no_fee') {
            if (!feeText.includes('no fee') && !feeText.includes('free') && !feeText.includes('none')) {
                return false;
            }
        } else if (filters.fee === 'up_to_500') {
            // Check if fee is numeric and <= 500
            const feeMatch = feeText.match(/(\d+)/g);
            if (feeMatch) {
                const feeAmount = parseInt(feeMatch[0]);
                if (feeAmount > 500) return false;
            } else if (!feeText.includes('no fee') && !feeText.includes('free')) {
                return false;
            }
        } else if (filters.fee === 'affordable') {
            // No fee OR up to 500
            const feeMatch = feeText.match(/(\d+)/g);
            const isNoFee = feeText.includes('no fee') || feeText.includes('free') || feeText.includes('none');
            if (!isNoFee && feeMatch) {
                const feeAmount = parseInt(feeMatch[0]);
                if (feeAmount > 500) return false;
            } else if (!isNoFee && !feeMatch) {
                return false;
            }
        }
    }

    // Language filter
    if (filters.language !== 'all') {
        const langText = (course.language || '').toLowerCase();
        if (filters.language === 'english') {
            if (!langText.includes('english')) return false;
        } else if (filters.language === 'german') {
            if (!langText.includes('german') && !langText.includes('deutsch')) return false;
        } else if (filters.language === 'en_de') {
            if (!langText.includes('english') && !langText.includes('german') && !langText.includes('deutsch')) {
                return false;
            }
        }
    }

    return true;
});

// Calculate filtered university counts
const universityCourseCounts = {};
filteredCourses.forEach(course => {
    if (!universityCourseCounts[course.university]) {
        universityCourseCounts[course.university] = 0;
    }
    universityCourseCounts[course.university]++;
});

// Update university data with filtered counts
const filteredUniversities = allUniversities.map((uni, idx) => {
    const filteredCount = universityCourseCounts[uni.name] || 0;
    if (filteredCount === 0) return null; // Hide universities with no matching courses

    // Determine category
    const name = uni.name.toLowerCase();
    let category = 'University';
    if (name.includes('hochschule') || name.includes('university of applied sciences') || name.includes('fachhochschule')) {
        category = 'University of Applied Sciences';
    } else if (name.includes('technische') || name.includes('technical university')) {
        category = 'Technical University';
    } else if (name.includes('kunst') || name.includes('art') || name.includes('music')) {
        category = 'Arts/Music University';
    }

    return {
        sn: idx + 1,
        name: uni.name,
        category: category,
        courses: filteredCount
    };
}).filter(uni => uni !== null)
  .sort((a, b) => a.name.localeCompare(b.name))
  .map((uni, idx) => ({ ...uni, sn: idx + 1 })); // Re-number after filtering

// Return updated data
return [
    { payload: filteredCourses.length, topic: "total_courses" },
    { payload: filteredUniversities.length, topic: "total_universities" },
    { payload: filteredUniversities, topic: "university_table" }
];

// Reset all filters to default
flow.set('courseFilters', {
    degree: ['all'],
    fee: 'all',
    language: 'all'
});

// Get original data
const courses = flow.get('courses') || [];
