const lastUpdated = flow.get('lastUpdated') || 'Never';

// Prepare original university data
const uniData = universities
    .sort((a, b) => a.name.localeCompare(b.name))
    .map((uni, idx) => {
        const name = uni.name.toLowerCase();
        let category = 'University';
        if (name.includes('hochschule') || name.includes('university of applied sciences') || name.includes('fachhochschule')) {
            category = 'University of Applied Sciences';
        } else if (name.includes('technische') || name.includes('technical university')) {
            category = 'Technical University';
        } else if (name.includes('kunst') || name.includes('art') || name.includes('music')) {
            category = 'Arts/Music University';
        }
        return {
            sn: idx + 1,
            name: uni.name,
            category: category,
            courses: uni.courses
        };
    });

// Send reset values to dropdowns and update dashboard
return [
