// Process DAAD Data

// Helper function to determine degree type from DAAD courseType
function getDegreeType(course) {
    const courseType = course.courseType;
    const courseName = (course.courseName || '').toLowerCase();

    // DAAD courseType mapping:
    // 1 = Bachelor's degree course
    // 2 = Master's degree course  
    // 3 = PhD course
    // 4 = Short course
    // 5-7 = Language/Preparation courses

    if (courseType === 1) return 'Bachelor';
    if (courseType === 2) return 'Master';
    if (courseType === 3) return 'PhD';

    // Fallback: analyze course name
    if (courseName.includes('bachelor')) return 'Bachelor';
    if (courseName.includes('master')) return 'Master';
    if (courseName.includes('phd') || courseName.includes('doctoral')) return 'PhD';

    return 'Other';
}

// 1) grab raw array and dedupe by raw ID
const raw = msg.payload.courses || [];
const dedupedById = {};
raw.forEach(c => {
    dedupedById[c.id] = c;
});
const unique = Object.values(dedupedById);

// 2) map to normalized courses
const timestamp = new Date().toISOString();
const courses = unique.map(course => ({
    id: `daad_${course.id}`,
    title: course.courseName,
    university: course.academy,
    country: course.country || 'Germany',
    degree: getDegreeType(course),
    language: (course.languages || []).join(', ') || 'English',
    source: 'DAAD',  // ‚Üê used by store to strip old entries
    url: `https://www2.daad.de${course.link}`,
    lastUpdated: timestamp,
    fees: course.tuitionFees || 'Varies',
    duration: course.programmeDuration || 'Not specified'
}));

// 3) group by university ‚Äî now with source!
const universityCounts = {};
courses.forEach(c => {
    if (!universityCounts[c.university]) {
        universityCounts[c.university] = {
            name: c.university,
            courses: 0,
            source: 'DAAD'       // ‚Üê tag it here
        };
    }
    universityCounts[c.university].courses++;
});
const universities = Object.values(universityCounts);

// 4) output payload with source
msg.payload = {
    source: 'DAAD',
    courses: courses,
    universities: universities,
    totalCourses: courses.length,
    timestamp: timestamp
};

// update node status
node.status({
    fill: "green",
    shape: "dot",
    text: `DAAD: ${courses.length} unique courses`
});

return msg;

// Get existing data from context
