// Process DAAD All Degree Programmes data

const raw = msg.payload.courses || [];
const timestamp = msg.payload.timestamp;

// Map to normalized course format
const courses = raw.map(course => ({
    id: `daad_all_${course.id}`,
    title: course.courseName,
    university: course.academy,
    country: course.country || 'Germany',
    degree: course.degreeType || 'Master',
    language: (course.languages || []).join(', ') || 'English',
    source: 'DAAD_All',  // ‚Üê used by store to strip old entries
    url: `https://www.daad.de${course.link}`,
    lastUpdated: timestamp,
    fees: course.tuitionFees || 'No tuition fees',
    duration: course.programmeDuration || 'Not specified'
}));

// Group by university
const universityCounts = {};
courses.forEach(c => {
    if (!universityCounts[c.university]) {
        universityCounts[c.university] = {
            name: c.university,
            courses: 0,
            source: 'DAAD_All'  // ‚Üê tag it here
        };
    }
    universityCounts[c.university].courses++;
});
const universities = Object.values(universityCounts);

// Output payload with source
msg.payload = {
    source: 'DAAD_All',
    courses: courses,
    universities: universities,
    totalCourses: courses.length,
    timestamp: timestamp
};

// Update node status
node.status({
    fill: "blue",
    shape: "dot",
    text: `Processed: ${courses.length} extended programmes`
});

return msg;
