        { payload: stats.totalCourses, topic: "total_courses" },
        { payload: stats.totalUniversities, topic: "total_universities" },
        { payload: stats.lastUpdated, topic: "last_updated" },
        { payload: universityData, topic: "university_table" },
        { payload: newCourses.slice(0, 10), topic: "new_courses" }
