}

return msg;

const results = msg.payload.courses || [];
const timestamp = new Date().toISOString();

// Process each course
const courses = results.map(course => ({
    id: `daad_${course.id}`,
    title: course.courseName,
    university: course.academy,
    country: course.country || 'Germany',
    degree: course.degreeType || 'Master',
    language: course.languages ? course.languages.join(', ') : 'English',
    source: 'DAAD',
    url: `https://www2.daad.de${course.link}`,
    lastUpdated: timestamp,
    fees: course.tuitionFees || 'Varies',
    duration: course.programmeDuration || 'Not specified'
}));

// Group by university for statistics
const universityCounts = {};
courses.forEach(course => {
    const uni = course.university;
    if (!universityCounts[uni]) {
        universityCounts[uni] = { name: uni, courses: 0, country: course.country, source: 'DAAD' };
    }
    universityCounts[uni].courses++;
});

const universities = Object.values(universityCounts);

msg.payload = {
    courses: courses,
    universities: universities,
    totalCourses: courses.length,
    source: 'DAAD',
    timestamp: timestamp
};

node.status({fill: "green", shape: "dot", text: `DAAD: ${courses.length} courses`});

return msg;

// Get existing data from context
let allCourses = flow.get('courses') || [];
let allUniversities = flow.get('universities') || [];

const newData = msg.payload;
const source = newData.source;

// Remove old data from same source
allCourses = allCourses.filter(course => course.source !== source);
allUniversities = allUniversities.filter(uni => uni.source !== source);

// Add new data
allCourses = allCourses.concat(newData.courses);
allUniversities = allUniversities.concat(newData.universities);

// Store back to context
flow.set('courses', allCourses);
flow.set('universities', allUniversities);
flow.set('lastUpdated', new Date().toISOString());

// Also save to file for persistence
msg.payload = {
    courses: allCourses,
    universities: allUniversities,
    totalCourses: allCourses.length,
    totalUniversities: allUniversities.length,
    lastUpdated: flow.get('lastUpdated')
};

node.status({fill: "blue", shape: "dot", text: `Stored: ${allCourses.length} total courses`});

return msg;

// Get previously known course IDs
const previousIds = flow.get('knownCourseIds') || [];
const currentCourses = msg.payload.courses;
const currentIds = currentCourses.map(c => c.id);

// Determine current semester
const now = new Date();
const currentMonth = now.getMonth() + 1; // 1-12
const currentYear = now.getFullYear();
let currentSemester;

if (currentMonth >= 3 && currentMonth <= 8) {
    currentSemester = `Summer ${currentYear}`;
} else {
    currentSemester = currentMonth <= 2 ? `Winter ${currentYear-1}/${currentYear}` : `Winter ${currentYear}/${currentYear+1}`;
}

// Find new courses
const newCourseIds = currentIds.filter(id => !previousIds.includes(id));
const newCourses = currentCourses.filter(c => newCourseIds.includes(c.id));

// Enhanced new course data with semester info
const enhancedNewCourses = newCourses.map(course => ({
    ...course,
    semester: currentSemester,
    addedAt: new Date().toLocaleString('en-GB'),
    isRecentSemester: true,
    applicationDeadline: course.title.includes('Winter') ? 'July 15' : course.title.includes('Summer') ? 'January 15' : 'Check university website'
}));

if (enhancedNewCourses.length > 0) {
    // Update known IDs
    const allKnownIds = [...new Set([...previousIds, ...currentIds])];
    flow.set('knownCourseIds', allKnownIds);

    // Store new courses for notification with semester context
    let allNewCourses = flow.get('newCourses') || [];
    allNewCourses = [...enhancedNewCourses, ...allNewCourses].slice(0, 100); // Keep last 100
    flow.set('newCourses', allNewCourses);

    // Filter for current semester additions only
    const currentSemesterCourses = allNewCourses.filter(c => c.semester === currentSemester);

    msg.payload = {
        newCourses: currentSemesterCourses,
        allNewCourses: enhancedNewCourses,
        count: enhancedNewCourses.length,
        currentSemester: currentSemester,
        timestamp: new Date().toISOString()
    };

    node.status({fill: "orange", shape: "dot", text: `${enhancedNewCourses.length} new courses for ${currentSemester}!`});

    return msg;
} else {
    // Update known IDs even if no new courses
    flow.set('knownCourseIds', currentIds);

    // Still show recent semester courses even if no new ones detected
    let allNewCourses = flow.get('newCourses') || [];
    const currentSemesterCourses = allNewCourses.filter(c => c.semester === currentSemester);

    if (currentSemesterCourses.length > 0) {
        msg.payload = {
            newCourses: currentSemesterCourses,
            count: 0,
            currentSemester: currentSemester,
            timestamp: new Date().toISOString()
        };
        node.status({fill: "green", shape: "dot", text: `Showing ${currentSemesterCourses.length} courses for ${currentSemester}`});
        return msg;
    }

